/**
 * Styled navigation component.
 *
 * @typedef {object} NavProps
 * @property {boolean} [dashboardInView] - Indicates whether the dashboard is in view.
 * @property {boolean} [isReadr] - Indicates whether the component is for "Readr" organization.
 */

const Nav = /** @type {import('styled-components').ThemedStyledFunction<'nav', any, NavProps>} */ (
  styled.nav
)`
  position: fixed;
  z-index: 200;
  display: flex;
  padding: 8px 20px 0;
  top: 0;
  width: 100vw;
  height: 60px;
  justify-content: space-between;
  align-items: center;
  pointer-events: none;

  * {
    pointer-events: auto;
  }

  a {
    cursor: pointer;
  }
  @media (max-width: 1024px) {
    flex-direction: column;
    width: unset;
    > div {
      margin-top: 8px;
    }
  }
  ${({ dashboardInView }) =>
    !dashboardInView &&
    `
      top: 40px;
      @media (max-width: 1024px) {
        display: none;
      }
  `}
  ${({ isReadr }) => !isReadr && 'align-items: start;'}
`

const LinkButton = styled.a`
  display: flex;
`

/**
 * Styled div component.
 *
 * @typedef {object} DivProps
 * @property {boolean} [isReadr] - Indicates whether the component is for "Readr" organization.
 */

const ShareWrapper = /** @type {import('styled-components').ThemedStyledFunction<'div', any, DivProps>} */ (
  styled.div
)`
  position: relative;
  ${({ isReadr }) => !isReadr && 'margin-left: 4px;'}
`

/**
 * Styled image component.
 *
 * @typedef {object} ImageProps
 * @property {boolean} [isReadr] - Indicates whether the component is for "Readr" organization.
 * @property {boolean} [show] - Indicates whether the image should show or not.
 */
const StyledImage = /** @type {import('styled-components').ThemedStyledFunction<Image, any, ImageProps>} */ (
  styled(Image)
)`
  position: absolute;
  top: 30px;
  left: 0;
  cursor: pointer;
  width: 40px;
  height: 40px;

  @media (max-width: 1024px) {
    width: 24px;
    height: 24px;
  }

  ${({ show, isReadr }) =>
    show
      ? `
      transition-duration: 190ms;
      &:first-of-type {
        transform: translate3d(0, 20px, 0);
      }
      &:last-of-type {
        transform: translate3d(0, 70px, 0);
      }
      @media (max-width: 1024px) {
        &:first-of-type {
          transform: translate3d(0, 5px, 0);
        }
        &:last-of-type {
          transform: translate3d(0, 40px, 0);
        }
      }
      ${
        !isReadr &&
        `
        @media (max-width: 1024px) {
          width: 28px;
          height: 28px;
        }

    `
      }

    `
      : `
      visibility: hidden;
    `}
`

/**
 *
 * @param {Object} props
 * @param {boolean} props.dashboardInView
 * @returns {React.ReactElement}
 */
export const NavBar = ({ dashboardInView }) => {
  const [showShareIcon, setShowShareIcon] = useState(false)
  /** @type {React.MutableRefObject<null | HTMLDivElement>} */
  const shareIconRef = useRef(null)
  const device = useAppSelector((state) => state.ui.device)
  const isMobile = device !== 'desktop'
  useClickOutside(shareIconRef, () => {
    setShowShareIcon(false)
  })

  /** @type {React.MouseEventHandler} */
  const onShareFB = (e) => {
    e.stopPropagation()
    window.open(
      'https://www.facebook.com/share.php?u='.concat(
        encodeURIComponent(
          window.location.protocol +
            '//' +
            window.location.host +
            window.location.pathname
        )
      )
    )
    gtag.sendGAEvent('Click', {
      project: `share-facebook / ${device}`,
    })
  }

  /** @type {React.MouseEventHandler} */
  const onShareLine = (e) => {
    e.stopPropagation()
    window.open(
      'https://line.me/R/msg/text/?' +
        encodeURIComponent(document.title) +
        ' '.concat(
          encodeURIComponent(
            window.location.protocol +
              '//' +
              window.location.host +
              window.location.pathname
          )
        )
    )
    gtag.sendGAEvent('Click', {
      project: `share-line / ${device}`,
    })
  }

  const Logo = isReadr
    ? isMobile
      ? readrLogoMobileSvg
      : readrLogoSvg
    : isMirrorDaily
    ? mirrorDailyLogoSvg
    : isMirrorTV
    ? mirrorTVLogoSvg
    : isMobile
    ? mmLogoMobileSvg
    : mmLogoSvg

  return (
    <Nav dashboardInView={dashboardInView} isReadr={isReadr}>
      <LinkButton
        href={
          organization === 'readr-media'
            ? 'https://www.readr.tw/'
            : organization === 'mirror-daily'
            ? 'https://www.mirrordaily.news/'
            : organization === 'mirror-tv'
            ? 'https://www.mnews.tw/'
            : 'https://www.mirrormedia.mg/'
        }
        target="_blank"
        rel="noreferrer"
      >
        {Logo}
      </LinkButton>
      <ShareWrapper ref={shareIconRef} isReadr={isReadr}>
        <LinkButton
          onClick={() => {
            setShowShareIcon((value) => !value)
          }}
        >
          {isMobile ? shareIconMobileSvg : shareIconSvg}
        </LinkButton>
        <StyledImage
          show={showShareIcon}
          src={fbLogo}
          loader={imageLoader}
          alt="facebook logo"
          onClick={onShareFB}
          isReadr={isReadr}
        />
        <StyledImage
          show={showShareIcon}
          src={lineLogo}
          loader={imageLoader}
          alt="line logo"
          onClick={onShareLine}
          isReadr={isReadr}
        />
      </ShareWrapper>
    </Nav>
  )
}
